import * as api from './api'

export function register (data) {
  return api.post('/register', data)
}

export function login (data) {
  return api.post('/login', data)
}

// export function logout (tokenData, token) {
//   return api.post('/logout', tokenData, token)
// }

export function logout (tokenObj) {
  console.info('Logout em api/users');
  return api.post('/logout', tokenObj, tokenObj.token)
}
